    /**
     * configure_shortcodes
     *
     * @since 1.0.0
     *
     * @param array $shortcodes
     */
    public function configure_shortcodes($shortcodes) {
      array_push($shortcodes, new T2T_Shortcode_Location_List());
      
      return $shortcodes;
    }

    /**
     * HELPERS
     */

    public static function get_full_address($post_id) {
      $address_parts = array();

      array_push($address_parts, get_post_meta($post_id, "address"));
      array_push($address_parts, get_post_meta($post_id, "city"));
      array_push($address_parts, get_post_meta($post_id, "state"));
      array_push($address_parts, get_post_meta($post_id, "postal_code"));
      array_push($address_parts, get_post_meta($post_id, "country"));

      return join(", ", $address_parts);
    }
